#!/usr/bin/env node
//@ts-check
var fs = require('fs')

// let [_node, _js,  file_in, file_out] = process.argv
var file_in = process.argv[process.argv.length - 2]
var file_out = process.argv[process.argv.length - 1]
// console.log(process.argv)
// debugger;
fs.createReadStream(file_in)
    .pipe(fs.createWriteStream(file_out))

